import React, { useState, useEffect } from 'react';
import {
  Container,
  Typography,
  Box,
  Card,
  CardContent,
  Grid,
  Button,
  Chip,
  Avatar,
  Tab,
  Tabs,
  TextField,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Paper,
  Divider,
  IconButton,
  Fab,
} from '@mui/material';
import {
  Add as AddIcon,
  Visibility as VisibilityIcon,
  Favorite as FavoriteIcon,
  FavoriteBorder as FavoriteBorderIcon,
  Comment as CommentIcon,
  Share as ShareIcon,
  FilterList as FilterIcon,
  TrendingUp as TrendingIcon,
  School as SchoolIcon,
  Person as PersonIcon,
} from '@mui/icons-material';

const PostsPage = () => {
  const [activeTab, setActiveTab] = useState(0);
  const [posts, setPosts] = useState([]);
  const [newPostDialog, setNewPostDialog] = useState(false);
  const [newPost, setNewPost] = useState({
    title: '',
    content: '',
    post_type: 'help_request',
    tags: '',
    target_degree: '',
    target_region: '',
    services_offered: '',
    pricing_info: ''
  });

  // Ê®°ÊãüÂ∏ñÂ≠êÊï∞ÊçÆ
  const mockPosts = [
    {
      id: 1,
      user_id: 1,
      post_type: "mentor_offer",
      title: "ÊñØÂù¶Á¶èCSÁ°ïÂ£´ÔºåÊèê‰æõ‰∏ì‰∏öÁî≥ËØ∑ÊåáÂØº",
      content: "Â§ßÂÆ∂Â•ΩÔºÅÊàëÊòØ2023Âπ¥ÊØï‰∏öÁöÑÊñØÂù¶Á¶èËÆ°ÁÆóÊú∫ÁßëÂ≠¶Á°ïÂ£´ÔºåÂú®Áî≥ËØ∑ËøáÁ®ã‰∏≠ÁßØÁ¥Ø‰∫Ü‰∏∞ÂØåÁªèÈ™å„ÄÇÁé∞Âú®Êèê‰æõ‰ª•‰∏ãÊúçÂä°Ôºö\n\n‚Ä¢ Áî≥ËØ∑Á≠ñÁï•Âà∂ÂÆö\n‚Ä¢ Êñá‰π¶ÂÜô‰ΩúÊåáÂØº\n‚Ä¢ ÁÆÄÂéÜ‰ºòÂåñ\n‚Ä¢ Èù¢ËØïÂáÜÂ§á\n\nÊàëÁöÑËÉåÊôØÔºöÊú¨Áßë985ÔºåGPA 3.8ÔºåÊâòÁ¶è110ÔºåGRE 330„ÄÇÊàêÂäüÁî≥ËØ∑Âà∞ÊñØÂù¶Á¶è„ÄÅCMU„ÄÅ‰ºØÂÖãÂà©Á≠âÈ°∂Â∞ñÂ≠¶Ê†°„ÄÇ\n\nÊúçÂä°ÁâπËâ≤Ôºö\n‚úÖ ‰∏ÄÂØπ‰∏Ä‰∏™ÊÄßÂåñÊåáÂØº\n‚úÖ Êèê‰æõÂÆûÈôÖÊ°à‰æãÂèÇËÄÉ\n‚úÖ 24Â∞èÊó∂ÂÜÖÂõûÂ§ç\n‚úÖ Êª°ÊÑèÂ∫¶‰øùËØÅ",
      tags: ["CS", "ÊñØÂù¶Á¶è", "Á°ïÂ£´Áî≥ËØ∑", "Êñá‰π¶ÊåáÂØº"],
      target_degree: "master",
      target_region: "ÁæéÂõΩ",
      target_university: "ÊñØÂù¶Á¶èÂ§ßÂ≠¶",
      target_major: "ËÆ°ÁÆóÊú∫ÁßëÂ≠¶",
      services_offered: ["Áî≥ËØ∑Âí®ËØ¢", "Êñá‰π¶ÂÜô‰Ωú", "ÁÆÄÂéÜ‰øÆÊîπ"],
      pricing_info: {
        "Áî≥ËØ∑Âí®ËØ¢": "200/Â∞èÊó∂",
        "Êñá‰π¶ÂÜô‰Ωú": "1500/ÁØá",
        "ÁÆÄÂéÜ‰øÆÊîπ": "300/Ê¨°"
      },
      author: {
        id: 1,
        username: "alice_cs",
        full_name: "Alice Wang",
        avatar_url: "https://api.dicebear.com/7.x/avataaars/svg?seed=Alice",
        verified: true,
        rating: 4.9
      },
      view_count: 156,
      like_count: 23,
      comment_count: 8,
      created_at: "2024-07-20T10:30:00Z",
      is_featured: true,
      liked_by_user: false
    },
    {
      id: 2,
      user_id: 2,
      post_type: "help_request",
      title: "Ê±ÇÂä©ÔºÅ25Fall CSÁ°ïÂ£´Áî≥ËØ∑ÔºåÊ±ÇÂ≠¶ÈïøÂ≠¶ÂßêÊåáÂØº",
      content: "Â§ßÂÆ∂Â•ΩÔºåÊàëÊòØ985Êú¨ÁßëCS‰∏ì‰∏öÁöÑÂ§ß‰∏âÂ≠¶ÁîüÔºåÂáÜÂ§áÁî≥ËØ∑25FallÁöÑCSÁ°ïÂ£´È°πÁõÆ„ÄÇ\n\nÊàëÁöÑËÉåÊôØÔºö\n‚Ä¢ Êú¨ÁßëÔºöÊüê985Â§ßÂ≠¶ËÆ°ÁÆóÊú∫ÁßëÂ≠¶\n‚Ä¢ GPAÔºö3.6/4.0\n‚Ä¢ ÊâòÁ¶èÔºöËøòÊ≤°ËÄÉÔºàÈ¢ÑËÆ°ÁõÆÊ†á105+Ôºâ\n‚Ä¢ GREÔºöËøòÊ≤°ËÄÉÔºàÈ¢ÑËÆ°ÁõÆÊ†á320+Ôºâ\n‚Ä¢ Á†îÁ©∂ÁªèÂéÜÔºö‰∏ÄÁØá‰∫å‰ΩúËÆ∫ÊñáÂú®Êäï\n‚Ä¢ ÂÆû‰π†ÔºöÂ≠óËäÇË∑≥Âä®ÁÆóÊ≥ïÂÆû‰π†3‰∏™Êúà\n\nÁî≥ËØ∑ÁõÆÊ†áÔºö\n‚Ä¢ ÂÜ≤Âà∫ÔºöCMU„ÄÅStanford„ÄÅMIT\n‚Ä¢ ÂåπÈÖçÔºöUCSD„ÄÅUIUC„ÄÅUW\n‚Ä¢ ‰øùÂ∫ïÔºöËøòÊ≤°ÂÆö\n\nÂ∏åÊúõÂæóÂà∞Â∏ÆÂä©Ôºö\n1. ÈÄâÊ†°Âª∫ËÆÆÔºàÁâπÂà´ÊòØ‰øùÂ∫ïÂ≠¶Ê†°Ôºâ\n2. Êñá‰π¶ÂÜô‰ΩúÊåáÂØº\n3. ËÉåÊôØÊèêÂçáÂª∫ËÆÆ\n4. Êó∂Èó¥ËßÑÂàí\n\nÈ¢ÑÁÆóÔºö2000-5000ÂÖÉÔºåÂ∏åÊúõÊâæÂà∞ÂêàÈÄÇÁöÑÂ≠¶ÈïøÂ≠¶ÂßêÔºÅ",
      tags: ["25Fall", "CSÁ°ïÂ£´", "Ê±ÇÊåáÂØº", "985ËÉåÊôØ"],
      target_degree: "master",
      target_region: "ÁæéÂõΩ",
      target_major: "ËÆ°ÁÆóÊú∫ÁßëÂ≠¶",
      author: {
        id: 2,
        username: "student_cs",
        full_name: "Tom Li",
        avatar_url: "https://api.dicebear.com/7.x/avataaars/svg?seed=Tom",
        verified: false,
        rating: null
      },
      view_count: 89,
      like_count: 12,
      comment_count: 15,
      created_at: "2024-07-22T14:20:00Z",
      is_featured: false,
      liked_by_user: true
    },
    {
      id: 3,
      post_type: "mentor_offer",
      title: "Âìà‰ΩõÂïÜÂ≠¶Èô¢MBAÔºåÂïÜÁßëÁî≥ËØ∑ÂÖ®Â•óÊåáÂØº",
      content: "HiÂ§ßÂÆ∂Â•ΩÔºÅÊàëÊòØ2022Âπ¥ÊØï‰∏öÁöÑÂìà‰ΩõÂïÜÂ≠¶Èô¢MBAÔºåÁõÆÂâçÂú®Êë©Ê†πÂ§ßÈÄöÂ∑•‰Ωú„ÄÇ\n\nÊàëÁöÑÊúçÂä°ÂåÖÊã¨Ôºö\nüìã Áî≥ËØ∑Á≠ñÁï•ËßÑÂàí\nüìù EssayÂÜô‰ΩúÊåáÂØº\nüéØ Èù¢ËØïÊ®°ÊãüËÆ≠ÁªÉ\nüè´ ÈÄâÊ†°Âª∫ËÆÆ\nüíº ÁÆÄÂéÜ‰ºòÂåñ\n\nÊàêÂäüÊ°à‰æãÔºö\n‚Ä¢ Â∏ÆÂä©15+Â≠¶ÁîüÊãøÂà∞M7 offer\n‚Ä¢ Âπ≥ÂùáÊèêÂçáGMATÊàêÁª©30ÂàÜ\n‚Ä¢ Èù¢ËØïÈÄöËøáÁéá95%\n\nÁâπÂà´ÊìÖÈïøÔºö\n- ÈáëËûç/Âí®ËØ¢ËÉåÊôØÁî≥ËØ∑ËÄÖ\n- ËΩ¨‰∏ì‰∏öÁî≥ËØ∑ÊåáÂØº\n- Â∑•‰ΩúÁªèÈ™åÂåÖË£Ö\n\nÁé∞Âú®ÂºÄÊîæÈ¢ÑÁ∫¶ÔºåÂÖàÂà∞ÂÖàÂæóÔºÅ",
      tags: ["MBA", "Âìà‰ΩõÂïÜÂ≠¶Èô¢", "ÂïÜÁßëÁî≥ËØ∑", "Èù¢ËØïËæÖÂØº"],
      author: {
        id: 3,
        username: "harvard_mba",
        full_name: "Sarah Chen",
        avatar_url: "https://api.dicebear.com/7.x/avataaars/svg?seed=Sarah",
        verified: true,
        rating: 4.9
      },
      view_count: 234,
      like_count: 45,
      comment_count: 12,
      created_at: "2024-07-21T09:15:00Z",
      is_featured: true,
      liked_by_user: false
    }
  ];

  useEffect(() => {
    setPosts(mockPosts);
  }, []);

  const handleTabChange = (event, newValue) => {
    setActiveTab(newValue);
    // Ê†πÊçÆtabÁ≠õÈÄâÂ∏ñÂ≠ê
    if (newValue === 0) {
      setPosts(mockPosts);
    } else if (newValue === 1) {
      setPosts(mockPosts.filter(p => p.post_type === 'mentor_offer'));
    } else if (newValue === 2) {
      setPosts(mockPosts.filter(p => p.post_type === 'help_request'));
    }
  };

  const handleLike = (postId) => {
    setPosts(prev => prev.map(post => {
      if (post.id === postId) {
        return {
          ...post,
          liked_by_user: !post.liked_by_user,
          like_count: post.liked_by_user ? post.like_count - 1 : post.like_count + 1
        };
      }
      return post;
    }));
  };

  const handleCreatePost = () => {
    // Ê®°ÊãüÂàõÂª∫Â∏ñÂ≠ê
    const post = {
      id: Date.now(),
      ...newPost,
      tags: newPost.tags.split(',').map(tag => tag.trim()).filter(Boolean),
      author: {
        id: 999,
        username: "current_user",
        full_name: "ÂΩìÂâçÁî®Êà∑",
        avatar_url: "https://api.dicebear.com/7.x/avataaars/svg?seed=User",
        verified: false
      },
      view_count: 0,
      like_count: 0,
      comment_count: 0,
      created_at: new Date().toISOString(),
      is_featured: false,
      liked_by_user: false
    };

    setPosts(prev => [post, ...prev]);
    setNewPostDialog(false);
    setNewPost({
      title: '',
      content: '',
      post_type: 'help_request',
      tags: '',
      target_degree: '',
      target_region: '',
      services_offered: '',
      pricing_info: ''
    });
  };

  const formatTime = (dateString) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffInHours = Math.floor((now - date) / (1000 * 60 * 60));

    if (diffInHours < 1) return 'ÂàöÂàö';
    if (diffInHours < 24) return `${diffInHours}Â∞èÊó∂Ââç`;
    return `${Math.floor(diffInHours / 24)}Â§©Ââç`;
  };

  const PostCard = ({ post }) => (
    <Card sx={{ mb: 3, '&:hover': { boxShadow: 3 } }}>
      <CardContent>
        {/* ‰ΩúËÄÖ‰ø°ÊÅØ */}
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
          <Avatar
            src={post.author.avatar_url}
            sx={{ width: 48, height: 48, mr: 2 }}
          />
          <Box sx={{ flex: 1 }}>
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
              <Typography variant="subtitle1" fontWeight={600}>
                {post.author.full_name}
              </Typography>
              {post.author.verified && (
                <Chip 
                  label="Â∑≤ËÆ§ËØÅ" 
                  size="small" 
                  color="primary" 
                  sx={{ height: 20 }}
                />
              )}
              <Chip
                label={post.post_type === 'mentor_offer' ? 'ÂØºÂ∏à' : 'Ê±ÇÂä©'}
                size="small"
                color={post.post_type === 'mentor_offer' ? 'success' : 'info'}
                sx={{ height: 20 }}
              />
              {post.is_featured && (
                <Chip
                  label="Á≤æÈÄâ"
                  size="small"
                  color="warning"
                  sx={{ height: 20 }}
                />
              )}
            </Box>
            <Typography variant="body2" color="text.secondary">
              @{post.author.username} ¬∑ {formatTime(post.created_at)}
            </Typography>
          </Box>
          {post.author.rating && (
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 0.5 }}>
              <TrendingIcon fontSize="small" color="primary" />
              <Typography variant="body2" color="primary" fontWeight={600}>
                {post.author.rating}
              </Typography>
            </Box>
          )}
        </Box>

        {/* Â∏ñÂ≠êÊ†áÈ¢ò */}
        <Typography variant="h6" component="h3" gutterBottom>
          {post.title}
        </Typography>

        {/* Â∏ñÂ≠êÂÜÖÂÆπ */}
        <Typography 
          variant="body2" 
          color="text.secondary" 
          sx={{ 
            mb: 2,
            display: '-webkit-box',
            WebkitLineClamp: 4,
            WebkitBoxOrient: 'vertical',
            overflow: 'hidden'
          }}
        >
          {post.content}
        </Typography>

        {/* Ê†áÁ≠æ */}
        <Box sx={{ mb: 2 }}>
          {post.tags?.map((tag) => (
            <Chip
              key={tag}
              label={tag}
              size="small"
              sx={{ mr: 1, mb: 1 }}
              variant="outlined"
              color="primary"
            />
          ))}
        </Box>

        {/* ÊúçÂä°‰ª∑Ê†º‰ø°ÊÅØÔºà‰ªÖÂØºÂ∏àÂ∏ñÂ≠êÊòæÁ§∫Ôºâ */}
        {post.post_type === 'mentor_offer' && post.pricing_info && (
          <Paper sx={{ p: 2, mb: 2, bgcolor: 'grey.50' }}>
            <Typography variant="subtitle2" gutterBottom color="primary">
              ÊúçÂä°‰ª∑Ê†º
            </Typography>
            <Box sx={{ display: 'flex', gap: 2, flexWrap: 'wrap' }}>
              {Object.entries(post.pricing_info).map(([service, price]) => (
                <Typography key={service} variant="body2">
                  <strong>{service}:</strong> {price}
                </Typography>
              ))}
            </Box>
          </Paper>
        )}

        <Divider sx={{ my: 2 }} />

        {/* ‰∫íÂä®ÊåâÈíÆ */}
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <Box sx={{ display: 'flex', gap: 2 }}>
            <Button
              size="small"
              startIcon={post.liked_by_user ? <FavoriteIcon /> : <FavoriteBorderIcon />}
              color={post.liked_by_user ? "error" : "inherit"}
              onClick={() => handleLike(post.id)}
            >
              {post.like_count}
            </Button>
            <Button
              size="small"
              startIcon={<CommentIcon />}
              color="inherit"
            >
              {post.comment_count}
            </Button>
            <Button
              size="small"
              startIcon={<VisibilityIcon />}
              color="inherit"
            >
              {post.view_count}
            </Button>
            <Button
              size="small"
              startIcon={<ShareIcon />}
              color="inherit"
            >
              ÂàÜ‰∫´
            </Button>
          </Box>

          {post.post_type === 'mentor_offer' ? (
            <Button variant="contained" size="small">
              ËÅîÁ≥ªÂØºÂ∏à
            </Button>
          ) : (
            <Button variant="outlined" size="small">
              Êèê‰æõÂ∏ÆÂä©
            </Button>
          )}
        </Box>
      </CardContent>
    </Card>
  );

  return (
    <Container maxWidth="lg" sx={{ py: 4 }}>
      {/* È°µÈù¢Ê†áÈ¢ò */}
      <Box sx={{ mb: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Á§æÂå∫ÂπøÂú∫
        </Typography>
        <Typography color="text.secondary">
          ÂàÜ‰∫´Áî≥ËØ∑ÁªèÈ™åÔºåÂØªÊâæÁî≥ËØ∑‰ºô‰º¥ÔºåËé∑Âæó‰∏ì‰∏öÊåáÂØº
        </Typography>
      </Box>

      <Grid container spacing={3}>
        {/* ‰∏ªË¶ÅÂÜÖÂÆπÂå∫ */}
        <Grid item xs={12} md={8}>
          {/* Ê†áÁ≠æÈ°µ */}
          <Paper sx={{ mb: 3 }}>
            <Tabs
              value={activeTab}
              onChange={handleTabChange}
              variant="fullWidth"
              sx={{ borderBottom: 1, borderColor: 'divider' }}
            >
              <Tab 
                label="ÂÖ®ÈÉ®" 
                icon={<PersonIcon />} 
                iconPosition="start"
              />
              <Tab 
                label="ÂØºÂ∏àÊúçÂä°" 
                icon={<SchoolIcon />} 
                iconPosition="start"
              />
              <Tab 
                label="Ê±ÇÂä©Â∏ñ" 
                icon={<PersonIcon />} 
                iconPosition="start"
              />
            </Tabs>
          </Paper>

          {/* Â∏ñÂ≠êÂàóË°® */}
          <Box>
            {posts.map((post) => (
              <PostCard key={post.id} post={post} />
            ))}
          </Box>
        </Grid>

        {/* ‰æßËæπÊ†è */}
        <Grid item xs={12} md={4}>
          {/* ÂèëÂ∏ÉÊåâÈíÆ */}
          <Card sx={{ mb: 3 }}>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                ÂàÜ‰∫´‰Ω†ÁöÑÊïÖ‰∫ã
              </Typography>
              <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
                ÂàÜ‰∫´Áî≥ËØ∑ÁªèÈ™åÔºåÊèê‰æõ‰∏ì‰∏öÊåáÂØºÔºåÊàñÂØªÊ±ÇÂ∏ÆÂä©
              </Typography>
              <Button
                variant="contained"
                fullWidth
                startIcon={<AddIcon />}
                onClick={() => setNewPostDialog(true)}
              >
                ÂèëÂ∏ÉÂ∏ñÂ≠ê
              </Button>
            </CardContent>
          </Card>

          {/* ÁÉ≠Èó®ËØùÈ¢ò */}
          <Card sx={{ mb: 3 }}>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                ÁÉ≠Èó®ËØùÈ¢ò
              </Typography>
              <Box sx={{ display: 'flex', flexDirection: 'column', gap: 1 }}>
                {[
                  { tag: '25FallÁî≥ËØ∑', count: 156 },
                  { tag: 'CSÁ°ïÂ£´', count: 89 },
                  { tag: 'MBAÁî≥ËØ∑', count: 67 },
                  { tag: 'Êñá‰π¶ÂÜô‰Ωú', count: 45 },
                  { tag: 'Èù¢ËØïÁªèÈ™å', count: 38 }
                ].map((topic) => (
                  <Button
                    key={topic.tag}
                    variant="text"
                    size="small"
                    sx={{ justifyContent: 'space-between' }}
                  >
                    <span>#{topic.tag}</span>
                    <span>{topic.count}</span>
                  </Button>
                ))}
              </Box>
            </CardContent>
          </Card>

          {/* Êé®ËçêÂØºÂ∏à */}
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Êé®ËçêÂØºÂ∏à
              </Typography>
              <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
                {mockPosts
                  .filter(p => p.post_type === 'mentor_offer')
                  .slice(0, 3)
                  .map((post) => (
                    <Box key={post.id} sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                      <Avatar
                        src={post.author.avatar_url}
                        sx={{ width: 32, height: 32 }}
                      />
                      <Box sx={{ flex: 1 }}>
                        <Typography variant="body2" fontWeight={600}>
                          {post.author.full_name}
                        </Typography>
                        <Typography variant="caption" color="text.secondary">
                          {post.title.substring(0, 20)}...
                        </Typography>
                      </Box>
                      <Button size="small" variant="outlined">
                        ÂÖ≥Ê≥®
                      </Button>
                    </Box>
                  ))}
              </Box>
            </CardContent>
          </Card>
        </Grid>
      </Grid>

      {/* ÂèëÂ∏ÉÂ∏ñÂ≠êÂØπËØùÊ°Ü */}
      <Dialog
        open={newPostDialog}
        onClose={() => setNewPostDialog(false)}
        maxWidth="md"
        fullWidth
      >
        <DialogTitle>ÂèëÂ∏ÉÊñ∞Â∏ñÂ≠ê</DialogTitle>
        <DialogContent>
          <Box sx={{ pt: 1 }}>
            {/* Â∏ñÂ≠êÁ±ªÂûã */}
            <FormControl fullWidth sx={{ mb: 2 }}>
              <InputLabel>Â∏ñÂ≠êÁ±ªÂûã</InputLabel>
              <Select
                value={newPost.post_type}
                label="Â∏ñÂ≠êÁ±ªÂûã"
                onChange={(e) => setNewPost(prev => ({ ...prev, post_type: e.target.value }))}
              >
                <MenuItem value="help_request">Ê±ÇÂä©Â∏ñ</MenuItem>
                <MenuItem value="mentor_offer">ÂØºÂ∏àÊúçÂä°</MenuItem>
              </Select>
            </FormControl>

            {/* Ê†áÈ¢ò */}
            <TextField
              fullWidth
              label="Ê†áÈ¢ò"
